import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import bgImage from "../images/bg.jpg"; // Ensure the correct path
import "./Home.css"; // Add this for styling

function Home() {
  const navigate = useNavigate();
  const text = "Welcome to the Virtual Herbal Garden";
  // Categories
  const categories = ["Ayurveda", "Yoga & Naturopathy", "Unani", "Siddha", "Homeopathy"];

  // Features Data
  const features = [
    {
      title: "Herbal Remedies",
      description: "Find natural remedies for various symptoms using medicinal plants.",
      link: "/remedies",
    },
    {
      title: "Plant Growth Tracker",
      description: "Monitor and track the growth of your medicinal plants over time.",
      link: "/tracker",
    },
    {
      title: "Plant Encyclopedia",
      description: "Learn about various medicinal plants, their uses, and benefits.",
      link: "/plant-encyclopedia",
    },
  ];


      return (
      <div>
      {/* Video Background */}
      <div style={{ position: "relative", width: "100%", height: "100vh", overflow: "hidden" }}>
  <iframe
    style={{
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      zIndex: -1,
      border: "none",
      pointerEvents: "none",
    }}
    src="https://www.youtube.com/embed/ppR_QhxqgEE?si=MsdperUrjBRusRBi"
    allow="autoplay; encrypted-media"
  ></iframe>


        {/* Overlay */}
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            zIndex: 0,
          }}
        ></div>

        {/* Heading & Description */}
        <div style={{ position: "relative", zIndex: 1, textAlign: "center", color: "white", paddingTop: "10%" }}>
          <h1 style={{ fontSize: "2.8rem", fontWeight: "bold" }}>Welcome to the Virtual Herbal Garden</h1>
          <p style={{ fontSize: "1.2rem", marginTop: "10px" }}>
            Discover medicinal plants, track their growth, and explore natural remedies.
          </p>
        </div>
      </div>
    
            {/* Categories Section */}
      <h2 className="section-title"></h2>
      <div className="categories-container">
        {categories.map((category) => (
          <motion.div
            key={category}
            onClick={() => navigate(`/plants/${category}`)}
            className="category-card"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {category}
          </motion.div>
        ))}
      </div>
      {/* Features Section with 3D Flip Cards */}
      <h2 className="section-title" style={{ textAlign: "center" }}>Explore Our Features</h2>
      <div className="features-container">
          {features.map((feature, index) => (
          <div key={index} className="flip-card" onClick={() => navigate(feature.link)}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <h3>{feature.title}</h3>
              </div>
              <div className="flip-card-back">
                <p>{feature.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      
    </div>
  );
}

export default Home;
