import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import bgImage from "../images/bg.jpg"; // Ensure correct path

function Home() {
  const navigate = useNavigate();

  // Categories with unique colors
  const categories = [
    { name: "Ayurveda", color: "#FFDDC1" },
    { name: "Yoga & Naturopathy", color: "#D4E157" },
    { name: "Unani", color: "#81D4FA" },
    { name: "Siddha", color: "#FFAB91" },
    { name: "Homeopathy", color: "#B39DDB" },
  ];

  const text = "Welcome to the Virtual Herbal Garden";

  return (
    <div>
      {/* Hero Section with Background Image */}
      <div
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "50vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          position: "relative",
          overflow: "hidden",
        }}
      >
        {/* Overlay for better readability */}
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            zIndex: 1,
          }}
        ></div>

        {/* Typewriter Effect */}
        <div style={{ zIndex: 2, maxWidth: "80%" }}>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", whiteSpace: "nowrap" }}>
            {text.split("").map((char, index) => (
              <motion.span
                key={index}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.05, delay: index * 0.1 }}
              >
                {char}
              </motion.span>
            ))}
          </h1>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, ease: "easeOut", delay: 2 }}
            style={{ fontSize: "1.2rem", marginTop: "10px" }}
          >
            Explore a rich collection of medicinal plants categorized under Ayurveda, Yoga & Naturopathy, Unani, Siddha, and Homeopathy.
          </motion.p>
        </div>
      </div>

      {/* Categories Section (Grid-Based Cards with Different Colors) */}
      <div
        style={{
          padding: "40px",
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "20px",
        }}
      >
        {categories.map((category) => (
          <motion.div
            key={category.name}
            onClick={() => navigate(`/plants/${category.name}`)}
            style={{
              borderRadius: "10px",
              boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
              textAlign: "center",
              background: category.color,
              fontSize: "1.2rem",
              fontWeight: "bold",
              cursor: "pointer",
              padding: "30px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              transition: "0.3s",
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {category.name}
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default Home;
