import React, { useEffect, useState } from "react";

function Home() {
  const [plants, setPlants] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(""); // Track selected category

  useEffect(() => {
    fetch("http://localhost:5000/api/plants")
      .then((response) => response.json())
      .then((data) => setPlants(data))
      .catch((error) => console.error("Error fetching plants:", error));
  }, []);

  // Extract unique categories from plants
  const categories = [...new Set(plants.map((plant) => plant.category))];

  // Filter plants based on selected category
  const filteredPlants = selectedCategory
    ? plants.filter((plant) => plant.category === selectedCategory)
    : [];

  return (
    <div>
      <h1>Virtual Herbal Garden</h1>

      {/* Category Selection Dropdown */}
      <label>Select a Category: </label>
      <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
        <option value="">-- Select --</option>
        {categories.map((category) => (
          <option key={category} value={category}>
            {category}
          </option>
        ))}
      </select>

      {/* Display Plants Only When a Category is Selected */}
      {selectedCategory && (
        <div>
          <h2>{selectedCategory}</h2>
          <ul>
            {filteredPlants.map((plant) => (
              <li key={plant._id}>
                <strong>{plant.name}</strong> - {plant.scientificName}
                <br />
                Benefits: {plant.benefits.join(", ")}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Home;
