import React from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();

  // Categories for the sections
  const categories = ["Ayurveda", "Yoga & Naturopathy", "Unani", "Siddha", "Homeopathy"];

  return (
    <div>
      {/* Hero Section with Background Image */}
      <div
        style={{
          backgroundImage: "url('/assets/background.jpg')", // Update with your image path
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "50vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          position: "relative",
        }}
      >
        {/* Overlay for better readability */}
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)", // Dark overlay
            zIndex: 1,
          }}
        ></div>

        {/* Text Content */}
        <div style={{ zIndex: 2, maxWidth: "80%" }}>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "bold" }}>Welcome to the Virtual Herbal Garden</h1>
          <p style={{ fontSize: "1.2rem", marginTop: "10px" }}>
            Explore a rich collection of medicinal plants categorized under Ayurveda, Yoga & Naturopathy, Unani, Siddha, and Homeopathy.
          </p>
        </div>
      </div>

      {/* Categories Section */}
      <div style={{ padding: "40px", display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px" }}>
        {categories.map((category) => (
          <div
            key={category}
            onClick={() => navigate(`/plants/${category}`)}
            style={{
              border: "1px solid #ccc",
              padding: "20px",
              cursor: "pointer",
              textAlign: "center",
              background: "#f0f0f0",
              fontSize: "1.2rem",
              fontWeight: "bold",
              transition: "0.3s",
            }}
            onMouseOver={(e) => (e.target.style.background = "#ddd")}
            onMouseOut={(e) => (e.target.style.background = "#f0f0f0")}
          >
            {category}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
