import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import bgImage from "../images/bg.jpg"; // Ensure the correct path

function Home() {
  const navigate = useNavigate();

  // Categories for the sections
  const categories = ["Ayurveda", "Yoga & Naturopathy", "Unani", "Siddha", "Homeopathy"];

  return (
    <div>
      {/* Hero Section with Background Image */}
      <div
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "50vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          position: "relative",
          overflow: "hidden",
        }}
      >
        {/* Overlay for better readability */}
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)", // Dark overlay
            zIndex: 1,
          }}
        ></div>

        {/* Animated Text Content */}
        <div style={{ zIndex: 2, maxWidth: "80%" }}>
          <motion.h1
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, ease: "easeOut" }}
            style={{ fontSize: "2.5rem", fontWeight: "bold" }}
          >
            Welcome to the Virtual Herbal Garden
          </motion.h1>
          <motion.p
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.5 }}
            style={{ fontSize: "1.2rem", marginTop: "10px" }}
          >
            Explore a rich collection of medicinal plants categorized under Ayurveda, Yoga & Naturopathy, Unani, Siddha, and Homeopathy.
          </motion.p>
        </div>
      </div>

      {/* Categories Section */}
      <div style={{ padding: "40px", display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px" }}>
        {categories.map((category) => (
          <motion.div
            key={category}
            onClick={() => navigate(`/plants/${category}`)}
            style={{
              border: "1px solid #ccc",
              padding: "20px",
              cursor: "pointer",
              textAlign: "center",
              background: "#f0f0f0",
              fontSize: "1.2rem",
              fontWeight: "bold",
              transition: "0.3s",
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {category}
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default Home;
