import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import bgImage from "../images/bg.jpg"; // Ensure correct path

function Home() {
  const navigate = useNavigate();
  const categories = ["Ayurveda", "Yoga & Naturopathy", "Unani", "Siddha", "Homeopathy"];
  
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % categories.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev - 1 + categories.length) % categories.length);
  };

  return (
    <div>
      {/* Hero Section */}
      <div
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "50vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          position: "relative",
          overflow: "hidden",
        }}
      >
        <div style={{ position: "absolute", width: "100%", height: "100%", backgroundColor: "rgba(0, 0, 0, 0.5)" }}></div>
        <div style={{ zIndex: 2, maxWidth: "80%" }}>
          <motion.h1
            style={{ fontSize: "2.5rem", fontWeight: "bold", whiteSpace: "nowrap" }}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
          >
            Welcome to the Virtual Herbal Garden
          </motion.h1>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 1 }}
            style={{ fontSize: "1.2rem", marginTop: "10px" }}
          >
            Explore a rich collection of medicinal plants categorized under Ayurveda, Yoga & Naturopathy, Unani, Siddha, and Homeopathy.
          </motion.p>
        </div>
      </div>

      {/* Category Slides */}
      <div style={{ position: "relative", width: "100%", height: "200px", overflow: "hidden", display: "flex", alignItems: "center", justifyContent: "center", marginTop: "40px" }}>
        {/* Previous Button */}
        <button
          onClick={prevSlide}
          style={{ position: "absolute", left: "20px", zIndex: 2, background: "rgba(0,0,0,0.5)", color: "white", border: "none", padding: "10px", cursor: "pointer" }}
        >
          ❮
        </button>

        {/* Slide Animation */}
        <AnimatePresence mode="wait">
          <motion.div
            key={categories[currentIndex]}
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -100, opacity: 0 }}
            transition={{ duration: 0.5 }}
            onClick={() => navigate(`/plants/${categories[currentIndex]}`)}
            style={{
              border: "2px solid #ccc",
              padding: "20px",
              textAlign: "center",
              background: "#f0f0f0",
              fontSize: "1.5rem",
              fontWeight: "bold",
              cursor: "pointer",
              width: "300px",
              height: "100px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              borderRadius: "10px",
            }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {categories[currentIndex]}
          </motion.div>
        </AnimatePresence>

        {/* Next Button */}
        <button
          onClick={nextSlide}
          style={{ position: "absolute", right: "20px", zIndex: 2, background: "rgba(0,0,0,0.5)", color: "white", border: "none", padding: "10px", cursor: "pointer" }}
        >
          ❯
        </button>
      </div>
    </div>
  );
}

export default Home;
