import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import bgImage from "../images/bg.jpg"; // Ensure the correct path

function Home() {
  const navigate = useNavigate();

  // Categories
  const categories = ["Ayurveda", "Yoga & Naturopathy", "Unani", "Siddha", "Homeopathy"];

  const text = "Welcome to the Virtual Herbal Garden";

  return (
    <div>
      {/* Hero Section with Background Image */}
      <div
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "50vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          position: "relative",
          overflow: "hidden",
        }}
      >
        {/* Overlay for better readability */}
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            zIndex: 1,
          }}
        ></div>

        {/* Typewriter Effect */}
        <div style={{ zIndex: 2, maxWidth: "80%" }}>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", whiteSpace: "nowrap" }}>
            {text.split("").map((char, index) => (
              <motion.span
                key={index}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.05, delay: index * 0.1 }}
              >
                {char}
              </motion.span>
            ))}
          </h1>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, ease: "easeOut", delay: 2 }}
            style={{ fontSize: "1.2rem", marginTop: "10px" }}
          >
            Explore a rich collection of medicinal plants categorized under Ayurveda, Yoga & Naturopathy, Unani, Siddha, and Homeopathy.
          </motion.p>
        </div>
      </div>

      {/* Categories Section (Grid-Based Cards with Classic Green) */}
      <div
        style={{
          padding: "40px",
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "20px",
        }}
      >
        {categories.map((category) => (
          <motion.div
            key={category}
            onClick={() => navigate(`/plants/${category}`)}
            style={{
              borderRadius: "10px",
              boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
              textAlign: "center",
              background: "#357960", // Classic Green
              fontSize: "1.2rem",
              fontWeight: "bold",
              cursor: "pointer",
              padding: "30px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "#fff",
              transition: "0.3s",
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {category}
          </motion.div>
        ))}
      </div>

      {/* Features Section with 3D Cards */}
      <div style={{ padding: "40px", textAlign: "center" }}>
        <h2 style={{ fontSize: "2rem", marginBottom: "20px", color: "#2d572c" }}>Explore Our Features</h2>
        
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "20px",
            justifyContent: "center",
          }}
        >
          {/* Feature Cards */}
          {[
            { title: "Herbal Remedies", description: "Find natural cures for various ailments.", link: "/remedies" },
            { title: "Plant Growth Tracker", description: "Monitor and track plant growth efficiently.", link: "/tracker" }
          ].map((feature, index) => (
            <motion.div
              key={index}
              onClick={() => navigate(feature.link)}
              style={{
                borderRadius: "10px",
                boxShadow: "0 10px 20px rgba(0,0,0,0.2)",
                textAlign: "center",
                background: "#4CAF50",
                color: "#fff",
                fontSize: "1.2rem",
                fontWeight: "bold",
                cursor: "pointer",
                padding: "20px",
                perspective: "1000px",
                transformStyle: "preserve-3d",
                transition: "0.3s",
              }}
              whileHover={{ scale: 1.05, rotateY: 10 }}
              whileTap={{ scale: 0.95, rotateY: -10 }}
            >
              <h3>{feature.title}</h3>
              <p style={{ fontSize: "1rem", fontWeight: "normal", marginTop: "10px" }}>{feature.description}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
