import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import bgImage from "../images/bg.jpg"; // Ensure the correct path

function Home() {
  const navigate = useNavigate();
  const categories = ["Ayurveda", "Yoga & Naturopathy", "Unani", "Siddha", "Homeopathy"];

  return (
    <div>
      {/* Navbar */}
      <nav
        style={{
          background: "rgba(0, 0, 0, 0.8)",
          padding: "10px 20px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "white",
          fontSize: "1.5rem",
          fontWeight: "bold",
          cursor: "pointer",
        }}
        onClick={() => navigate("/")} // Clicking AyurFlora will take you home
      >
        AyurFlora
      </nav>

      {/* Hero Section */}
      <div
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "50vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          position: "relative",
        }}
      >
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)", // Overlay for readability
          }}
        ></div>

        <div style={{ zIndex: 2 }}>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "bold" }}>Welcome to the Virtual Herbal Garden</h1>
          <p style={{ fontSize: "1.2rem", marginTop: "10px" }}>
            Explore a rich collection of medicinal plants categorized under Ayurveda, Yoga & Naturopathy, Unani, Siddha, and Homeopathy.
          </p>
        </div>
      </div>

      {/* Categories Section */}
      <div style={{ padding: "40px", display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px" }}>
        {categories.map((category) => (
          <motion.div
            key={category}
            onClick={() => navigate(`/plants/${category}`)}
            style={{
              border: "1px solid #ccc",
              padding: "20px",
              cursor: "pointer",
              textAlign: "center",
              background: "#f0f0f0",
              fontSize: "1.2rem",
              fontWeight: "bold",
              transition: "0.3s",
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {category}
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default Home;
